mapper.update:
UPDATE "BOARD" SET 
BOARD_TITLE = #{boardTitle},
BOARD_CONTENT = #{boardContent}
WHERE BOARD_CODE = #{boardCode}
AND BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

UPDATE "BOARD" SET
BOARD_DEL_FL = 'Y'
WHERE BOARD_CODE = #{boardCode}
AND BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

mapper.select:
     	SELECT * FROM (
	       SELECT 
	           BOARD_NO AS "currentBoardNo",
	           LAG(BOARD_NO) OVER (ORDER BY BOARD_NO) AS "prevBoardNo",

	           LEAD(BOARD_NO) OVER (ORDER BY BOARD_NO) AS "nextBoardNo"
	       FROM "BOARD"
	       WHERE BOARD_CODE = #{boardCode}
	     )
	     WHERE "currentBoardNo" = #{boardNo}
