mapper.select:
SELECT COUNT(*) FROM "BOARD" WHERE BOARD_DEL_FL = 'N'
AND SPORTS_CODE = #{sportsCode}
AND BOARD_CODE = 1

SELECT SPORTS_KR_NAME
FROM SPORTS_TYPE
WHERE SPORTS_CODE = #{sportsCode}

SELECT COUNT(*)
FROM ATTEND_MEMBER
WHERE BOARD_NO = #{boardNo}

SELECT COUNT(*)
FROM ATTEND_MEMBER
WHERE BOARD_NO = ${boardNo}
AND MEMBER_NO = ${memberNo}

mapper.delete:
DELETE FROM "BOARD_LIKE"
WHERE MEMBER_NO = #{memberNo}
AND BOARD_NO = #{boardNo}

mapper.update:
UPDATE "BOARD" SET
	BOARD_DEL_FL = 'Y'
WHERE SPORTS_CODE = #{sportsCode}
AND BOARD_NO = #{boardNo}
